mapper.select:
SELECT id, title, status, due_month, due_date, created_at FROM tasks
AND due_month = #{due_month}
ORDER BY status ASC, due_date ASC

mapper.update:
        UPDATE tasks

				title = #{title}

         WHERE 1=1
         AND id = #{id}

UPDATE tasks
status = CASE WHEN status = 1 THEN 2 WHEN status = 2 THEN 1 ELSE status END
WHERE id = #{id}
